mapper.select:
SELECT IF(COUNT(*) = 1, 1, 0)
FROM Member
WHERE member_email = #{memberEmail}

SELECT member_id, member_type_id, member_email, member_name, member_password, member_birth
FROM Member
WHERE member_email = #{memberEmail}

SELECT member_id, member_type_id, member_email, member_name, member_password, member_birth
FROM Member
WHERE member_id = #{memberId}
